const r = document.getElementsByTagName("svg")[0];
function fig(x, y, b) {
  for(let i = 0; i < 4; i++) {
    const e = document.createElementNS("http://www.w3.org/2000/svg", "use");
    e.setAttributeNS("http://www.w3.org/1999/xlink", "href", "#" + ((b >> i) & 1));
    e.setAttribute("x", x);
    e.setAttribute("y", y + i * 4);
    r.appendChild(e);
  }
}

const x = crypto.getRandomValues(new Uint16Array(1))[0];
const a = (x >>  0) & 15;
const b = (x >>  4) & 15;
const c = (x >>  8) & 15;
const d = (x >> 12) & 15;
const e = ((x >> 0) & 1) | ((x >> 3) & 2) | ((x >> 6) & 4) | ((x >>  9) & 8);
const f = ((x >> 1) & 1) | ((x >> 4) & 2) | ((x >> 7) & 4) | ((x >> 10) & 8);
const g = ((x >> 2) & 1) | ((x >> 5) & 2) | ((x >> 8) & 4) | ((x >> 11) & 8);
const h = ((x >> 3) & 1) | ((x >> 6) & 2) | ((x >> 9) & 4) | ((x >> 12) & 8);

fig(19.5, 42.0, a);
fig( 3.0, 66.5, b);
fig(19.5, 82.0, c);
fig(44.0, 66.5, d);
fig(68.5, 82.0, e);
fig(85.0, 66.5, f);
fig(68.5, 42.0, g);
fig(85.0, 17.5, h);
fig(68.5,  2.0, a ^ b);
fig(44.0, 17.5, c ^ d);
fig(19.5,  2.0, e ^ f);
fig( 3.0, 17.5, g ^ h);
