svg.script:
const m = crypto.getRandomValues(new Uint16Array(1))[0];
const r = document.getElementsByTagName("svg")[0];

// Only the low 16 bits of m are set. So if b>=16, then (m>>b)=0, so the
// XOR does nothing.
function fig(x, y, s, a, b=16) {
  for(let i = 0; i < 4; a += s, b += s, y += 4, i++) {
    const e = document.createElementNS("http://www.w3.org/2000/svg", "use");
    e.setAttributeNS("http://www.w3.org/1999/xlink", "href", "#" + (((m >> a) ^ (m >> b)) & 1));
    e.setAttribute("x", x);
    e.setAttribute("y", y);
    r.appendChild(e);
  }
}

fig(19.5, 42.0, 1,  0);
fig( 3.0, 66.5, 1,  4);
fig(19.5, 82.0, 1,  8);
fig(44.0, 66.5, 1, 12);
fig(68.5, 82.0, 4,  0);
fig(85.0, 66.5, 4,  1);
fig(68.5, 42.0, 4,  2);
fig(85.0, 17.5, 4,  3);
fig(68.5,  2.0, 1,  0,  4);
fig(44.0, 17.5, 1,  8, 12);
fig(19.5,  2.0, 4,  0,  1);
fig( 3.0, 17.5, 4,  2,  3);
